mapper.select:
		select 
			rno, 
    		rid,rstar,rdate,rcontent,USERS_UID,BOOkS_ISBN	
		From(
		    select rownum as rno, 
	    		rid,rstar,rdate,rcontent,USERS_UID,BOOkS_ISBN
		    From (
		        Select 
					rid,rstar,rdate,rcontent,USERS_UID,BOOkS_ISBN
				From reviews
				Where BOOKS_ISBN=#{booksIsbn}
				order by rdate desc
				)
		    where  rownum <= #{endRowNo} 
		    )
		where rno >= #{startRowNo}
